// GrillaProductos.js
import './GrillaGames.css';
import Games from '../data/games.json';
import Game from './Game';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css'; // Importar los estilos básicos de Swiper
import 'swiper/css/navigation'; // Importar los estilos de navegación
import 'swiper/css/pagination'; // Importar los estilos de paginación
import SwiperCore, { Navigation, Pagination } from 'swiper';

// Instalar los módulos de navegación y paginación
SwiperCore.use([Navigation, Pagination]);

export default function GrillaProductos() {
    return (
        <Swiper
            spaceBetween={10}
            slidesPerView={4}
            loop
            pagination={{ clickable: true }} // Configuración de paginación
            breakpoints={{
                320: {
                    slidesPerView: 1,
                },
                768: {
                    slidesPerView: 2,
                },
                1024: {
                    slidesPerView: 4,
                },
            }}
        >
            {Games.map((game, index) => (
                <SwiperSlide className='swiper-games' key={index}>
                    <Game
                        image={game.image}
                        title={game.title}
                        new_price={game.new_price}
                        free_ship={game.free_ship}
                    />
                </SwiperSlide>
            ))}
        </Swiper>
    );
}
