import React from 'react';
import './GrillaPost.css';
import Posts from '../data/posts.json';
import Post from './Post';

export default function GrillaPost() {
    return (
        <>
            {Posts.map((post, index) => (
                <section className="grid-post" key={index}>
                    <Post 
                        image={post.image}
                        title={post.title}
                        type={post.type}
                    />
                </section>
            ))}
        </>
    );
}
